SELECT retweet_count, textbody, posting_user, category, sub_category FROM tweets , users  WHERE posting_user = screen_name AND MONTH(posted) = 1 AND YEAR(posted) = 2016 ORDER BY retweet_count DESC LIMIT 5;
SELECT COUNT(DISTINCT users.state) AS statenum, group_concat(DISTINCT users.state ORDER BY users.state) as states, hashtags.hashtagname as hashtag_text FROM hashtags JOIN tweets ON tweets.tid = hashtags.tid JOIN users AS users ON users.screen_name = tweets.posting_user WHERE users.state != "" and users.state != "na" and year(tweets.posted) = '2016' GROUP BY hashtags.hashtagname ORDER BY statenum DESC LIMIT 5;
select screen_name as screen_name, state as state from users where screen_name in(select posting_user from tweets where tid in(select tid from hashtags where hashtagname = 'HappyNewYear' or hashtagname = 'NewYear' or hashtagname = 'NewYears' or hashtagname = 'NewYearsDay')) order by numFollowers desc LIMIT 12;
select screen_name as screen_name, sub_category as subcategory ,numFollowers as numFollowers from users where sub_category in('GOP', 'Democrat') order by numFollowers desc LIMIT 5;
select distinct hashtags.hashtagname, users.state from hashtags inner join tweets on hashtags.tid = tweets.tid inner join users on  users.screen_name = tweets.posting_user where users.state IN ('Ohio', 'Alaska', 'Alabama') AND MONTH(tweets.posted) = 1 AND year(tweets.posted) = 2016 ;
select tweets.textbody, hashtags.hashtagname, users.screen_name, users.sub_category from project2.hashtags inner join project2.tweets on hashtags.tid=tweets.tid inner join project2.users on  users.screen_name = tweets.posting_user where hashtags.hashtagname = 'Ohio' AND users.state = 'Ohio' AND MONTH(tweets.posted) = 1 AND year(tweets.posted) = 2016 order by tweets.tid limit 5;
SELECT DISTINCT users.screen_name as ScreenName, users.state as State, group_concat(url.url) as ListOfURLs FROM url INNER JOIN tweets ON (url.tid=tweets.tid) INNER JOIN users ON (users.screen_name=tweets.posting_user) WHERE users.sub_category = 'GOP' AND month(tweets.posted) = 1 AND year(tweets.posted) = 2016 GROUP BY users.screen_name;
SELECT usertweets.screen_name, users.state, group_concat(DISTINCT users.screen_name) FROM users INNER JOIN tweets ON (users.screen_name=tweets.posting_user) INNER JOIN usertweets ON (usertweets.tid=tweets.tid) WHERE users.sub_category = 'GOP' AND month(tweets.posted) = 1 AND year(tweets.posted) = 2016 GROUP BY usertweets.screen_name ORDER BY count(usertweets.screen_name) DESC LIMIT 5;
select hashtagname as hashtag_text, count(distinct tweets.tid) as num_uses from hashtags as hashtags join tweets on tweets.tid = hashtags.tid join users on users.screen_name = tweets.posting_user where users.sub_category = "GOP" and year(tweets.posted) = 2016 and month(tweets.posted) between 1 and 3 group by hashtags.hashtagname order by count(distinct tweets.tid) desc limit 5;